package ls.cookbook.repository;

import ls.cookbook.domain.Recipe;
import ls.cookbook.domain.RecipeCategory;
import org.springframework.stereotype.Repository;

import java.util.HashSet;
import java.util.Set;

@Repository
public class CookbookRepository {

    private final Set<Recipe> cookbook;

    public CookbookRepository() {
        cookbook = new HashSet<>();
        cookbook.add(new Recipe("Pełnia Schabu", "2kg schabu, 3kg ziemniaków, sałatka colesław", RecipeCategory.MEALS, "data:image/jpeg;base64,"));
        cookbook.add(new Recipe("Zupa Mocy", "5kg kości wołowych, 1xdynia. Gotujj kości 2 dni potem do wywaru dodaj pokrojoną dynie. Jak zmięknie blendujemy. Przypraw solą do smaku", RecipeCategory.SOUPS, "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSFmLlGWmqN2K6tUFSehWvzWrK8BwfvfwwpCQ&usqp=CAU"));
        cookbook.add(new Recipe("Wakacyjna beza", "10 jajek, garść owoców leśnych. Z jajek zrób bezę i dodaj owoce", RecipeCategory.DESSERTS, "data:image/jpeg;base64,"));
    }


    public Set<Recipe> findByCategory(RecipeCategory category) {
        Set<Recipe> result = new HashSet<>();
        for (Recipe recipe : cookbook) {
            if (recipe.getCategory().equals(category))
                result.add(recipe);
        }
        return result;
    }

    public Set<Recipe> findAll() {
        return cookbook;
    }
}
